import * as React from "react";
import { Link } from "react-router-dom";
import { FaMoneyCheckAlt, FaUser } from "react-icons/fa";
import { MdDashboard } from "react-icons/md";
import { IoMdStats, IoMdDocument, IoMdChatboxes } from "react-icons/io";
import { BiSupport } from "react-icons/bi";
import { useHistory } from "react-router-dom";
import "./MenuPannel.scss";

interface ILink {
  text: string;
  key: string;
  icon: React.ReactElement;
  href?: string;
}

type ArrayValue = Array<ILink>;

type SubTitle = "Tools" | "Other";

type Menus = Record<SubTitle, ArrayValue>;
const menus = {
  Tools: [
    {
      text: "DashBoard",
      key: "",
      icon: <MdDashboard />,
    },
    {
      text: "Finances",
      key: "finance",
      icon: <FaMoneyCheckAlt />,
    },
    {
      text: "People",
      key: "people",
      icon: <FaUser />,
    },
    {
      text: "Statistics",
      key: "stat",
      icon: <IoMdStats />,
    },
    {
      text: "Documents",
      key: "docs",
      icon: <IoMdDocument />,
    },
  ],
  Other: [
    {
      text: "Chat",
      key: "chat",
      icon: <IoMdChatboxes />,
    },
    {
      text: "Support",
      key: "Support",
      icon: <BiSupport />,
    },
  ],
};

Object.freeze(menus);

type Props = {};

const MenuPannel: React.FC<Props> = ({}) => {
  const [history, setHistory] = React.useState("");
  const history1 = useHistory();

  React.useEffect(() => {
    history1.listen(() => {
      // console.log(this);
      // console.log(history);
    });
  }, [history]);
  return (
    <div className="MenuPannel">
      <h3 className="title">HBoard</h3>
      <div className="section section1">
        {Object.keys(menus as Menus).map((item) => {
          return (
            <React.Fragment key={item}>
              <p className="title">{item}</p>
              <ul className="menu-list">
                {(menus as any)[item as SubTitle].map((menu: ILink) => (
                  <li
                    className={`menu-list__item item1 ${
                      history && (history as any).location.pathname.split("/")[1] === menu.key ? "selected" : ""
                    }`}
                    key={menu.key}
                  >
                    <Link to={menu.key}>
                      {menu.icon}
                      <span>{menu.text}</span>
                    </Link>
                  </li>
                ))}
              </ul>
            </React.Fragment>
          );
        })}
        <p className="title">Tools</p>
        <ul className="menu-list">
          <li className="menu-list__item item1 selected">menu 1</li>
          <li className="menu-list__item item2">menu 2</li>
          <li className="menu-list__item item3">menu 3</li>
          <li className="menu-list__item item4">menu 4</li>
          <li className="menu-list__item item5">menu 5</li>
        </ul>
      </div>
      <div className="section section2">
        <p className="title">Tools</p>
        <ul className="menu-list">
          <li className="menu-list__item item1">menu 1</li>
          <li className="menu-list__item item2">menu 2</li>
        </ul>
      </div>
    </div>
  );
};

export default MenuPannel;
