import React from "react";
import { FaMoneyCheckAlt, FaUser } from "react-icons/fa";
import { MdDashboard } from "react-icons/md";
import { IoMdStats, IoMdDocument, IoMdChatboxes } from "react-icons/io";
import { BiSupport } from "react-icons/bi";
import "./MenuPannel.scss";

interface Link {
  text: string;
  key: string;
  icon: React.ReactElement;
}

type ArrayValue = Array<Link>;

type SubTitle = "Tools" | "Other";

type Menus = Record<SubTitle, ArrayValue>;
const menus = {
  Tools: [
    {
      text: "DashBoard",
      key: "dashboard",
      icon: <MdDashboard />,
    },
    {
      text: "Finances",
      key: "finance",
      icon: <FaMoneyCheckAlt />,
    },
    {
      text: "People",
      key: "people",
      icon: <FaUser />,
    },
    {
      text: "Statistics",
      key: "stat",
      icon: <IoMdStats />,
    },
    {
      text: "Documents",
      key: "docs",
      icon: <IoMdDocument />,
    },
  ],
  Other: [
    {
      text: "Chat",
      key: "chat",
      icon: <IoMdChatboxes />,
    },
    {
      text: "Support",
      key: "Support",
      icon: <BiSupport />,
    },
  ],
};

Object.freeze(menus);

const MenuPannel: React.FC = () => {
  return (
    <div className="MenuPannel">
      <h3 className="title">HBoard</h3>
      <div className="section section1">
        {Object.keys(menus as Menus).map((item) => {
          return (
            <React.Fragment>
              <p className="title">{item}</p>
              <ul className="menu-list">
                {(menus as any)[item as SubTitle].map((item2: Link) => (
                  <li className="menu-list__item item1">
                    {item2.icon}
                    <span>{item2.text}</span>
                  </li>
                ))}
              </ul>
            </React.Fragment>
          );
        })}
        <p className="title">Tools</p>
        <ul className="menu-list">
          <li className="menu-list__item item1 selected">menu 1</li>
          <li className="menu-list__item item2">menu 2</li>
          <li className="menu-list__item item3">menu 3</li>
          <li className="menu-list__item item4">menu 4</li>
          <li className="menu-list__item item5">menu 5</li>
        </ul>
      </div>
      <div className="section section2">
        <p className="title">Tools</p>
        <ul className="menu-list">
          <li className="menu-list__item item1">menu 1</li>
          <li className="menu-list__item item2">menu 2</li>
        </ul>
      </div>
    </div>
  );
};

export default MenuPannel;
